create database Managerment_Page
go
use Managerment_Page
go
create table Account 
(
	idAccount int primary key identity,
	username varchar(50) unique not null,
	password varchar(50) not null,
)
create table Account_Detail 
(
	idAccount int ,
	fullname nvarchar(max) not null,
	DOB date not null,
	Address nvarchar(50) not null,
	Email varchar(50) not null,
	status bit default '1',
	imgAccount nvarchar(max) default 'default-avatar.jpg',
	primary key (idAccount),
	foreign key (idAccount) references Account(idAccount),
)

create table Type_Comic
(
	idTypeComic int identity primary key,
	nameTypeComic nvarchar(max) not null,
)
create table [User]
(
	idAccount int primary key identity,
	username varchar(50) unique not null,
	password varchar(50) not null,
)
create table User_Detail
(
	idAccount int ,
	fullname nvarchar(max) not null,
	DOB date not null,
	Address nvarchar(50) not null,
	Email varchar(50) not null,
	status bit default '1',
	imgAccount nvarchar(max) default 'default-avatar.jpg',
	primary key (idAccount),
	foreign key (idAccount) references [User](idAccount),
)
create table Forgot_Password (
	idForgot_Password int identity primary key,
	Email varchar(50) not null,
)

create table Comic
(
	idComic int identity primary key, -- Mã Comic
	nameComic nvarchar(max) not null,-- Tên Comic
	imgComic nvarchar(max) not null,-- Ảnh đại diện
	idTypeComic int not null, -- Loại Comic
	decription nvarchar(max) not null, -- Mô tả
	author nvarchar(max) not null,-- Tác giả
	idAccount int default '1', -- Người đăng
	status bit default '1',
	foreign key (idTypeComic) references Type_Comic(idTypeComic),
	foreign key (idAccount) references Account(idAccount)
)
create table Chuong
(
	idChuong int identity,
	tenChuong nvarchar(max) not null,
	idComic int not null,
	content nvarchar(max) not null,
	status bit default '1',
	primary key (idChuong),
	foreign key (idComic) references Comic(idComic),
)
create table ListFavourite
(
	idFavourite int identity,
	idAccount int not null,
	idComic int not null,
	primary key (idFavourite,idAccount, idComic),
	foreign key (idAccount) references [User](idAccount),
	foreign key (idComic) references Comic(idComic),
)
create proc TinhTiLeTaiKhoan
as
begin
declare @countStatustrue float(1) set @countStatustrue = (Select count(*) from Account_Detail where status ='1')
declare @countAll float(1) set @countAll = (Select count(*) from Account_Detail)
declare @persent float set @persent = (select (@countStatustrue / @countAll)*100)
select format(@persent, 'N2')
end
exec TinhTiLeTaiKhoan
-----------------tinhTiLEComicACtive------------------------
create proc TinhTiLeComicActive
as
begin
declare @countStatustrue float(1)  set @countStatustrue = (Select count(*) from Comic where status = '1')
declare @counAll float(1) set @counAll = (select count(*) from Comic)
declare @persent float set @persent = (select (@countStatustrue / @counAll)*100 )
select format(@persent, 'N2')
end
exec TinhTiLeComicActive
------------------TinhTiLEChuoNGACTIVE------------------------
create proc TinhTiLeChuongActive
as
begin
declare @countStatustrue float(1)  set @countStatustrue = (Select count(*) from Chuong where [status] = '1')
declare @counAll float(1) set @counAll = (select count(*) from Chuong)
declare @persent float set @persent = (select (@countStatustrue / @counAll)*100 )
select format(@persent, 'N2')
end

------------------TinhTiLEUSERACTIVE------------------------
create proc TinhTiLeUserActive
as
begin
declare @countStatustrue float(1)  set @countStatustrue = (Select count(*) from User_Detail where status = '1')
declare @counAll float(1) set @counAll = (select count(*) from User_Detail)
declare @persent float set @persent = (select (@countStatustrue / @counAll)*100 )
select format(@persent, 'N2')
end
